a = ''
b = ''
la = len(a)
lb = len(b)
f = [[0 for i in range(lb+1)] for j in range(la+1)]
for i in range(1, la+1):
    for j in range(1, lb+1):
        if a[i-1] == b[j-1]:
            f[i][j] = f[i-1][j-1] + 1
        else:
            if f[i-1][j] > f[i][j-1]:
                f[i][j] = f[i-1][j]
            else:
                f[i][j] = f[i][j-1]
print(f[la][lb])